mapper.select:
SELECT *
FROM USERT
WHERE 1=1

	AND user_buso = #{user_buso}


	AND user_jick = #{user_jick}


	AND user_name LIKE CONCAT('%', #{user_name}, '%')


	AND user_ret = #{user_ret}

SELECT *
FROM PERMISSION
WHERE USER_CODE = #{user_code}

SELECT *
FROM tb_permission
WHERE user_code = #{user_code}

SELECT menu_yn
FROM tb_usermenu
WHERE 1=1
AND user_code =
#{user_code}
AND menu_url = #{menu_url}

mapper.update:
UPDATE
tb_usermenu SET menu_yn = 'N'
WHERE 1=1
AND user_code = #{user_code}
AND
menu_url = #{menu_url}

mapper.insert:
INSERT INTO LOGINHIS
VALUES (
(SELECT ISNULL(MAX(LOGIN_CODE), 0)+1
FROM LOGINHIS
),#{user_code}, #{user_ip}, CONVERT(vaRchar(20), GETDATE(),120)
)

mapper.select:
SELECT *
FROM tb_user
WHERE user_id = #{user_id}
AND user_code != #{user_code}

mapper.delete:
DELETE FROM
tb_user_worktime
WHERE
user_code = #{user_code, jdbcType=VARCHAR}
AND
work_day = #{work_day, jdbcType=VARCHAR}

mapper.update:
UPDATE tb_user
SET
device_token = #{device_token}
WHERE
user_id = #{user_id}
AND
user_pw =
#{user_pw}

mapper.insert:
INSERT INTO tb_user_group (
    user_code,
    group_id
) VALUES (
    #{user_code},
    #{group_id}
)

mapper.delete:
DELETE FROM tb_user_group
WHERE 
    user_code = #{user_code}
AND 
    group_id = #{group_id}

mapper.update:
UPDATE tb_alarmlist
SET is_send = '1'
WHERE regtime = #{regtime}

UPDATE tb_group SET
group_name = #{group_name}
WHERE group_id = #{group_id}
